function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),c=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),c.classList.remove("locked"),e.classList.remove("active-burger")):(s.classList.add("active"),e.classList.add("active-burger"),c.classList.add("locked"))}));document.querySelectorAll(".menu__item-link").forEach((function(t){t.addEventListener("click",(()=>{c.classList.remove("locked"),s.classList.remove("active"),e.classList.remove("active-burger"),console.log("afafafa")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked"))}))}burgerMenu();